$(function(){moment.locale("fa");$(".spinner-overLay").hide();var n=new Vue({el:"#mainApp",data:{pricePerGram:null,weightInGrams:null,totalPrice:null,priceLoad:{isLoading:!0,isLoadingError:!1,loadTimePersian:"",loadTimeText:"در حال دریافت اطلاعات"}},computed:{pure:function(){if(!this.checkInputsA())return this.pricePerGram*this.weightInGrams},purePlushVatAndProfit:function(){if(!this.checkInputsA())return this.pure*1.07*1.09},total:function(){if(!this.checkInputsB())return this.totalPrice},pureNoVatAndProfit:function(){if(!this.checkInputsB())return this.totalPrice/1.09/1.07},dif:function(){if(!this.checkInputsA()&&!this.checkInputsB())return this.pureNoVatAndProfit-this.pure}},methods:{checkInputsA:function(){return!this.pricePerGram||!this.weightInGrams},checkInputsB:function(){return!this.totalPrice},perGram:function(n){if(this.weightInGrams)return!n&&n!==0?void 0:n/this.weightInGrams},gotFocus:function(n){setTimeout(function(){n.target.select()},0)},loadPrice:function(){var n=this;axios.get("https://api.goldyar.com/price.json",{headers:{"Content-Type":"application/json"},timeout:5e3}).then(function(t){n.pricePerGram=t.data.price;n.priceLoad.loadTimeText="آخرین بروزرسانی: "+moment.utc(t.data.utcTime).fromNow()+"&nbsp;";n.priceLoad.loadTimePersian="("+t.data.persianTime+")";n.priceLoad.isLoading=!1}).catch(function(){n.priceLoad.loadTimeText="اطلاعات ناموجود";n.priceLoad.isLoadingError=!0;n.priceLoad.isLoading=!1})}},mounted:function(){var n=this;this.$nextTick(function(){setTimeout(function(){n.loadPrice()},0)})}})});