$(function(){moment.locale("fa");$(".spinner-overLay").hide();var n=new Vue({el:"#mainApp",data:{pricePerGram:null,priceLoad:{isLoading:!0,isLoadingError:!1,loadTimePersian:"",loadTimeText:"در حال دریافت اطلاعات"}},methods:{loadPrice:function(){var n=this;axios.get("https://nargilistudio.github.io/gy-api/price.json",{headers:{"Content-Type":"application/json"},timeout:5e3}).then(function(t){n.pricePerGram=t.data.price;n.priceLoad.loadTimeText="آخرین بروزرسانی: "+moment.utc(t.data.utcTime).fromNow()+"&nbsp;";n.priceLoad.loadTimePersian=t.data.persianTime;n.priceLoad.isLoading=!1}).catch(function(){n.priceLoad.loadTimeText="اطلاعات ناموجود";n.priceLoad.isLoadingError=!0;n.priceLoad.isLoading=!1})}},mounted:function(){var n=this;this.$nextTick(function(){setTimeout(function(){n.loadPrice()},0)})}})});